mapper.update:

	UPDATE 
		d_player_record dpr
	SET
		dpr.character_id = #{characterId},
		dpr.is_sp = #{isSp},
		dpr.apply_pioneer = #{applyPioneer}
		
		,dpr.use_card = #{useCard}
		
		
		,dpr.camp = #{camp}
		
		
		,dpr.sign = #{sign}
		
		
		,dpr.identity_desc = #{identityDesc}
		
	WHERE
		dpr.player_id = #{playerId}
